/*
 * Este es un nodo de Código (Code) en n8n.
 * Asume que los datos de entrada (items) son un array de objetos,
 * donde cada objeto tiene una propiedad `json` con `fecha_reservacion`.
 *
 * Ejemplo de entrada (un item):
 * {
 * "json": {
 * "fecha_reservacion": "2025-11-12",
 * "count": "3"
 * }
 * }
 *
 * El nodo recibirá MÚLTIPLES items de este tipo.
 */

// --- Helper Function ---
/**
 * Convierte un objeto Date a un string en formato YYYY-MM-DD.
 * @param {Date} d - El objeto Date a formatear.
 * @returns {string} - La fecha en formato YYYY-MM-DD.
 */
const toYYYYMMDD = (d) => {
  const year = d.getFullYear();
  // getMonth() es 0-indexado (0=Enero), por eso +1
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

// --- Main Logic ---

// 1. Obtener la fecha de hoy, normalizada a medianoche (hora local)
const today = new Date();
today.setHours(0, 0, 0, 0);

// 2. Procesar todos los items de entrada
const existingDates = new Set();
let maxDate = null;

for (const item of items) {
  const dateStr = item.json.fecha_reservacion;
  if (!dateStr) {
    continue; // Ignorar items sin fecha
  }

  // Añadir el string de la fecha al Set para búsqueda rápida
  existingDates.add(dateStr);

  // Encontrar la fecha máxima
  // Parsear la fecha como local. new Date(Y, M-1, D) es lo más seguro.
  const [year, month, day] = dateStr.split('-').map(Number);
  const currentDate = new Date(year, month - 1, day);

  if (!maxDate || currentDate > maxDate) {
    maxDate = currentDate;
  }
}

// 3. Verificar si tenemos una fecha máxima válida
if (!maxDate) {
  // No se encontraron fechas en la entrada
  return [];
}

// 4. Si la fecha máxima es anterior a hoy, no hay rango para verificar
if (maxDate < today) {
  return [];
}

// 5. Iterar desde "hoy" hasta "maxDate" (inclusive)
const missingDates = [];
// Clonamos 'today' para usarlo como nuestro iterador
const loopDate = new Date(today.getTime());

while (loopDate <= maxDate) {
  // Convertir la fecha del bucle a string YYYY-MM-DD
  const loopDateStr = toYYYYMMDD(loopDate);

  // Comprobar si esta fecha NO está en nuestra lista de entrada
  if (!existingDates.has(loopDateStr)) {
    // Si no está, la añadimos al resultado
    missingDates.push({
      json: {
        fecha_faltante: loopDateStr
      }
    });
  }

  // Avanzar al siguiente día
  loopDate.setDate(loopDate.getDate() + 1);
}

// 7. Devolver el array de objetos con las fechas faltantes
return missingDates;

