// n8n Code node (JavaScript)
// Convierte el arreglo Aggregate.data[*].message{type, content} en un objeto plano:
// { "Humano_1": "...", "IA_1": "...", "Humano_2": "...", "IA_2": "..." }

const items = $input.all();

// Limpia el texto: recorta, colapsa espacios y, si viene el bloque largo,
// extrae lo que sigue a "Mensaje de Texto o Descripción:"
function cleanText(s) {
  if (!s) return '';
  let t = String(s);

  const marker = 'Mensaje de Texto o Descripción';
  const idx = t.toLowerCase().indexOf(marker.toLowerCase());
  if (idx !== -1) {
    t = t.slice(idx + marker.length);
  }

  // Quita separadores iniciales y colapsa espacios/nuevas líneas
  t = t.replace(/^[\s:.-]+/, '').replace(/\s+/g, ' ').trim();
  return t;
}

// Extrae mensajes desde varias formas posibles
const records = [];
for (const it of items) {
  const j = it.json || {};

  // Si viene como { data: [...] } úsalo; si no, intenta interpretar el propio objeto
  const arr = Array.isArray(j.data) ? j.data : (Array.isArray(j) ? j : (j.data ? [j.data] : [j]));

  for (const row of arr) {
    const msg = row?.message || row;
    const type = (msg?.type || msg?.role || '').toLowerCase();
    const content = msg?.content ?? msg?.text ?? '';

    if (!type || !content) continue;

    const role = (type === 'human' || type === 'user') ? 'Humano' : 'IA';
    records.push({ role, text: cleanText(content) });
  }
}

// Construye objeto plano con claves enumeradas
const out = {};
let h = 1, a = 1;
for (const r of records) {
  if (r.role === 'Humano') out[`Humano_${h++}`] = r.text;
  else out[`IA_${a++}`] = r.text;
}

return [{ json: out }];

// Code (JavaScript) ANTES del Summarization Chain
// Convierte Humano_*/IA_* en un solo bloque de texto ordenado

const firstItem = $input.first();
if (!firstItem || !firstItem.json) {
  // Si por alguna razón no hay nada, devolvemos un texto vacío controlado
  return [{ json: { text: '' } }];
}

const it = firstItem.json;

const lines = Object.entries(it)
  .filter(([k]) => /^(Humano|IA)_(\d+)$/i.test(k))
  .sort(
    (a, b) =>
      parseInt(a[0].match(/\d+/)[0], 10) -
      parseInt(b[0].match(/\d+/)[0], 10),
  )
  .map(([k, v]) =>
    `${k.startsWith('Humano') ? 'Humano' : 'IA'}: ${String(v ?? '').trim()}`,
  );

// Si por lo que sea no hay líneas, igual devolvemos text, pero vacío
return [{ json: { text: lines.join('\n') || '' } }];

const item = $input.first() || { json: {} };
const j = item.json || {};
let text = j.text ?? '';

text = String(text).trim();

if (!text) {
  // Si no hay texto, devolvemos un mensaje mínimo para que el Summarization no truene
  text = 'No hay conversación disponible para resumir.';
}

return [{ json: { text } }];

// 1. Obtenemos el texto crudo
const text = $input.item.json.output;

// 2. Función auxiliar para extraer datos ignorando errores de JSON
function extraer(regex, fuente) {
  const match = fuente.match(regex);
  return match ? match[1].trim() : "No proporcionado";
}

// 3. Extraemos cada campo individualmente (usando Regex para evitar el error del JSON roto)
const queja = extraer(/"queja"\s*:\s*"([^"]+)"/i, text);
const intencion = extraer(/"intencion"\s*:\s*"([^"]+)"/i, text);
const datos_clave = extraer(/"datos_clave"\s*:\s*"([^"]+)"/i, text);
const contexto = extraer(/"contexto"\s*:\s*"([^"]+)"/i, text);
const proxima_accion = extraer(/"proxima_accion_recomendada"\s*:\s*"([^"]+)"/i, text);
const tono = extraer(/"tono_sentimiento"\s*:\s*"([^"]+)"/i, text);
const urgencia = extraer(/"urgencia"\s*:\s*"([^"]+)"/i, text);
const nivel = extraer(/"nivel_urgencia"\s*:\s*"?(\d+)"?/i, text);

// 4. Construimos el bloque de texto agrupado (Resumen Ejecutivo)
const resumenEjecutivo = `RESUMEN EJECUTIVO
• Intención: ${intencion}
• Datos clave: ${datos_clave}
• Contexto: ${contexto}
• Próxima acción recomendada: ${proxima_accion}

NOTAS OPERATIVAS
• Tono/sentimiento: ${tono}
• Urgencia: ${urgencia}
• Nivel de urgencia: ${nivel}`;

// 5. Retornamos TODO: campos sueltos y el resumen final
return {
  queja,
  intencion,
  datos_clave,
  contexto,
  proxima_accion_recomendada: proxima_accion,
  notas_operativas: {
    tono_sentimiento: tono,
    urgencia: urgencia,
    nivel_urgencia: nivel
  },
  resumen_final: resumenEjecutivo
};

